import { Component, AfterViewInit } from '@angular/core';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables); // Register Chart.js modules

@Component({
  selector: 'app-about',
  templateUrl: './about.component.html',
  styleUrls: ['./about.component.css'],
})
export class AboutComponent implements AfterViewInit {
  // Data for the charts
  chartData = [
    { id: 'beChart', label: 'Bachelor of Engineering', percentage: 78 },
    { id: 'hscChart', label: 'Higher Secondary Certificate', percentage: 80 },
    { id: 'sslcChart', label: 'Secondary School Leaving Certificate', percentage: 93.6 },
  ];

  ugPercentage = this.chartData[0].percentage;
  twelthPercentage = this.chartData[1].percentage;
  tenthPercentage = this.chartData[2].percentage;

  ngAfterViewInit(): void {
    // Iterate over the chartData array to create multiple charts
    this.chartData.forEach((data) => {
      this.createDonutChart(data.id, data.label, data.percentage);
    });
  }

  private createDonutChart(elementId: string, label: string, percentage: number): void {
    const ctx = document.getElementById(elementId) as HTMLCanvasElement;

    new Chart(ctx.getContext('2d')!, {
      type: 'doughnut',
      data: {
        labels: ['Achieved', 'Remaining'],
        datasets: [
          {
            label: label,
            data: [percentage, 100 - percentage],
            backgroundColor: ['rgba(75, 192, 192, 0.7)', 'rgba(192, 192, 192, 0.5)'],
            borderColor: ['rgba(75, 192, 192, 1)', 'rgba(192, 192, 192, 1)'],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            position: 'bottom',
          },
          tooltip: {
            callbacks: {
              label: (context) => `${context.label}: ${context.raw}%`,
            },
          },
        },
      },
    });
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
  <title>Education Timeline</title>
  <link
    rel="stylesheet"
    href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css"
  />
  <style>
    body {
      font-family: Arial, sans-serif;
      background-color: #1a1a1d;
      color: white;
      margin: 0;
      padding: 0;
    }

    .h3 {
      text-align: center;
      padding: 10px;
    }

    .timeline {
      position: relative;
      margin: 20px 0;
      padding: 10px 40px;
      border-left: 2px solid #fff;
    }

    .timeline-card {
      margin-bottom: 30px;
      position: relative;
    }

    .timeline-card::after {
      content: '';
      position: absolute;
      left: -9px;
      top: 0;
      width: 20px;
      height: 20px;
      background-color: #1a1a1d;
      border: 2px solid white;
      border-radius: 50%;
    }

    .timeline-icon {
      position: absolute;
      left: -36px;
      top: 0;
      width: 30px;
      height: 30px;
      background-color: white;
      color: #1a1a1d;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 50%;
      font-size: 16px;
    }

    .timeline-content {
      padding: 10px 20px;
      background-color: rgba(255, 255, 255, 0.1);
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(255, 255, 255, 0.2);
    }

    canvas {
      margin-top: 10px;
    }
  </style>
</head>
<body>
  <div class="h3 p-1 m-0" style="background-color: #1a1a1d00; position: sticky; top: 0; z-index: 1000;">
    <div class="heading" style="color: rgb(133, 130, 130);">
        Education
    </div>
  </div>

  <section id="education" class="container my-5">
    <div class="timeline">
      <!-- Bachelor's Timeline Card -->
      <div class="timeline-card">
        <div class="timeline-icon">
          <i class="fas fa-graduation-cap"></i>
        </div>
        <div class="timeline-content">
          <h3>Bachelor of Engineering in Electronics and Communication</h3>
          <p class="institution">Nellai College of Engineering ( Anna University )</p>
          <p class="duration">2018 - 2022</p>
          <p class="details">CGPA: 7.8/10</p>
          <canvas id="beChart" width="400" height="400"></canvas>
        </div>
      </div>

      <!-- HSC Timeline Card -->
      <div class="timeline-card">
        <div class="timeline-icon">
          <i class="fas fa-school"></i>
        </div>
        <div class="timeline-content">
          <h3>Higher Secondary Certificate (HSC)</h3>
          <p class="institution">Little Flower Matriculation Higher Secondary School</p>
          <p class="duration">2016 - 2018</p>
          <p class="details">Percentage: 80%</p>
          <canvas id="hscChart" width="400" height="400"></canvas>
        </div>
      </div>

      <!-- SSLC Timeline Card -->
      <div class="timeline-card">
        <div class="timeline-icon">
          <i class="fas fa-book"></i>
        </div>
        <div class="timeline-content">
          <h3>Secondary School Leaving Certificate (SSLC)</h3>
          <p class="institution">Ideal Matriculation School</p>
          <p class="duration">2015 - 2016</p>
          <p class="details">Percentage: 93.6%</p>
          <canvas id="sslcChart" width="400" height="400"></canvas>
        </div>
      </div>
    </div>
  </section>

  <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/gsap/3.12.2/gsap.min.js"></script>
  <script>
    // Chart.js Implementation
    function createDonutChart(elementId, label, percentage) {
      const ctx = document.getElementById(elementId).getContext('2d');
      new Chart(ctx, {
        type: 'doughnut',
        data: {
          labels: ['Achieved', 'Remaining'],
          datasets: [
            {
              label: label,
              data: [percentage, 100 - percentage],
              backgroundColor: ['rgba(75, 192, 192, 0.7)', 'rgba(192, 192, 192, 0.5)'],
              borderColor: ['rgba(75, 192, 192, 1)', 'rgba(192, 192, 192, 1)'],
              borderWidth: 1,
            },
          ],
        },
        options: {
          responsive: true,
          plugins: {
            legend: { position: 'bottom' },
            tooltip: {
              callbacks: {
                label: (context) => `${context.label}: ${context.raw}%`,
              },
            },
          },
        },
      });
    }

    createDonutChart('beChart', 'Bachelor of Engineering', 78);
    createDonutChart('hscChart', 'Higher Secondary Certificate', 80);
    createDonutChart('sslcChart', 'Secondary School Leaving Certificate', 93.6);

    // GSAP Animation
    gsap.from(".timeline-card", {
      opacity: 0,
      y: 50,
      duration: 1,
      stagger: 0.3,
    });
  </script>
</body>
</html>
