import { Component, OnInit, AfterViewInit, ElementRef, ViewChild, OnDestroy } from '@angular/core';
import * as THREE from 'three';
import NET from 'vanta/dist/vanta.net.min.js';

@Component({
  selector: 'app-vantaelement',
  template: `<div #vantaNet class="vanta-net-container">
    <ng-content></ng-content>
  </div>`,
  styles: [`
    .vanta-net-container {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100vh;
      z-index: -1;
    }
    .content-container {
      position: relative;
      z-index: 1;
    }
  `]
})
export class VantaEffectComponent implements OnInit, AfterViewInit, OnDestroy {
  @ViewChild('vantaNet') vantaNet!: ElementRef;
  vantaEffect: any;

  constructor() { }

  ngOnInit(): void { }

  ngAfterViewInit(): void {
    this.vantaEffect = NET({
      el: this.vantaNet.nativeElement,
      mouseControls: true,
      touchControls: true,
      gyroControls: false,
      minHeight: 200.00,
      minWidth: 200.00,
      scale: 1.00,
      scaleMobile: 1.00,
      THREE: THREE // Use the imported THREE object
    });
  }

  ngOnDestroy(): void {
    if (this.vantaEffect) {
      this.vantaEffect.destroy();
    }
  }
}
